mapper.select:
select sfb01 , sfb04 ,sfb08,sfb081,sfb09,sfb11,sfb12,sfb81 ,sfb13 ,sfb15, sfb25 ,sfb36 ,sfb38
from ${centre}.sfb_file
where sfb01 = #{code}

select sfb01,sfb02,sfb06,smydesc,sfb81,sfb05,sfb43,sfb44, gen02, sfb87,ima02 ,sfb04,sfb08,sfb081,sfb09,sfb12,sfb98, sfb102,gem02, #{centre} centre from ${centre}.sfb_file left join ${centre}.smy_file on smyslip = SUBSTR(sfb01,0,4) left join ${centre}.ima_file on ima01 = sfb05 left join ${centre}.gem_file on gem01 =sfb98 left join ${centre}.gen_file on gen01 = sfb44
and lower(sfb01) like lower('%${code}%')
and sfb81 >= #{startdate}
and sfb81 <= #{enddate}
order by sfb81,sfb01
