mapper.update:
update ${centre}.rvb_file
set rvb07 = #{rvb07},rvb33 = #{rvb07},rvb87 = #{rvb07},rvb88 = round( rvb10 * #{rvb07},2 ),rvb88t = round(rvb10t * #{rvb07},2)
where rvb01 = #{rvb01} and rvb02 = #{rvb02}

mapper.select:
select *
from ${centre}.rvb_file
where rvb01 = #{rvb01} and rvb02 = #{rvb02}
