mapper.update:
update ${centre}.TLF_FILE
set tlf10 = #{tlf10}, tlf18 = tlf18 + #{tlf18}
where tlf026 = #{tlf026} and tlf027 = #{tlf027} and tlf036 = #{tlf036} and tlf037 = #{tlf037}

update ${centre}.tlf_file
set tlf19 = #{tlf19}
where tlf026 = #{code} or tlf036 = #{code}

update ${centre}.tlf_file
set tlf14 = #{tlf14}
where ( tlf026 = #{code} or tlf036 = #{code} ) and tlf037 = #{tlf037}

update ${centre}.tlf_file
set tlf20 = #{tlf20}
where  tlf026 = #{code} or tlf036 = #{code}

update ${centre}.tlf_file
set tlf41 = #{tlf41}
where  ( tlf026 = #{code} or tlf036 = #{code} ) and tlf037 = #{tlf037}

update ${centre}.TLF_FILE
set tlf06 = #{ddate}, tlf07 = #{ddate}


        and tlf026 = #{tlf026}


        and tlf036 = #{tlf036}


        and tlf907 = #{tlf907}

mapper.select:
SELECT CPRODUCENO,ima02 cproducename,CDANHAO,nvl(sum(IQTY),0) iqty, nvl(tlf10,0) qty,CRTYPE, CWHCODE,imd02,(nvl(sum(IQTY),0) - nvl(tlf10,0)) gapqty FROM ( SELECT CPRODUCENO,CDANHAO,0 - IQTY iqty,0 - tlf10 tlf10,'O' CRTYPE,CWHCODE FROM ${centre}.TC_WDD_FILE LEFT JOIN ( SELECT TLF01,tlf907,tlf021,tlf026,tlf036,sum(tlf10) tlf10 FROM ${centre}.TLF_FILE GROUP BY TLF01,tlf907,tlf026,tlf036,tlf021 ) ON TLF01 = CPRODUCENO and tlf907 = -1 AND tlf021 = CWHCODE AND tlf026 = CDANHAO UNION ALL
SELECT CPRODUCENO,CDANHAO,IQTY,tlf10,'I' CRTYPE,CWHCODE FROM ${centre}.TC_WDB_FILE LEFT JOIN ( SELECT TLF01,tlf907,tlf031,tlf026,tlf036,sum(tlf10) tlf10 FROM ${centre}.TLF_FILE GROUP BY TLF01,tlf907,tlf026,tlf036,tlf031 ) ON TLF01 = CPRODUCENO AND tlf907 = 1 AND tlf031 = CWHCODE AND tlf036 = CDANHAO ) a LEFT JOIN ${centre}.IMA_FILE ON a.CPRODUCENO = IMA01 LEFT JOIN ${centre}.imd_file ON imd01 = a.CWHCODE
and lower(a.CDANHAO) like lower('%${code}%')
and a.CPRODUCENO = #{ima}
and a.CWHCODE = #{imd}
GROUP BY CPRODUCENO,CDANHAO,tlf10,CRTYPE,CWHCODE,imd02,ima02
