mapper.select:
select zx01, zx02 , LISTAGG(zw01, ', ') WITHIN GROUP (ORDER BY zw01) zx04, LISTAGG(zw02, ', ') WITHIN GROUP (ORDER BY zw02) zw02, zxy03, azp02, zx03 gem01, gem02, zx08 from ZX_FILE left join ${centre}.GEM_FILE on gem01 = zx03 left join ZXW_FILE on zxw01 = zx01 and zxw03 = 1 left join ZW_FILE on zw01 = nvl(zxw04, zx04) left join (select zxy01, LISTAGG(zxy03, ', ') WITHIN GROUP (ORDER BY zxy01) zxy03, LISTAGG(nvl(azp02, zx08), ', ') WITHIN GROUP (ORDER BY zxy01) azp02 from ZXY_FILE left join AZP_FILE on azp01 = zxy03 left join ZX_FILE on zx01 = zxy01 group by zxy01) on zxy01 = zx01
and zxacti = 'Y' and zx18 is null
and zx08 = #{centre}
and ( zx01 like '%${zx}%' or zx02 like '%${zx}%' )
and zxacti = #{acti}
group by zx01, zx02, zxy03,azp02,zx03,gem02,zx08
